mapper.select:

		SELECT COUNT(*)
		FROM EMPLOYEE
		JOIN RANK USING(RID)
        JOIN DEPARTMENT USING(DID)



					AND ENAME LIKE #{sv}


					AND DNAME LIKE #{sv}


					AND RNAME LIKE #{sv}


			AND STATUS='N'

		ORDER BY RID DESC


